import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const MaintenanceForm = ({ isOpen, onClose, assetId, onSave }) => {
  const [formData, setFormData] = useState({
    assetId: assetId || '',
    description: '',
    performedBy: '',
    date: new Date().toISOString().split('T')[0],
    status: 'completed'
  });
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch assets for dropdown
    api.assets.getAll()
      .then(data => {
        const normalizedAssets = (data.assets || []).map(asset => ({
          ...asset,
          assetId: asset.asset_id || asset.assetId,
        }));
        setAssets(normalizedAssets);
      })
      .catch(err => console.error('Failed to load assets:', err));
  }, []);

  useEffect(() => {
    if (assetId) {
      setFormData(prev => ({ ...prev, assetId }));
    }
  }, [assetId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setError('');
  };

  const handleSubmit = async () => {
    if (!formData.assetId || !formData.description) {
      setError('Asset and description are required');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Backend expects assetId (camelCase) in the request
      const maintenanceData = {
        assetId: formData.assetId,
        description: formData.description,
        performedBy: formData.performedBy,
        date: formData.date,
        status: formData.status,
      };
      
      const result = await api.maintenance.create(maintenanceData);

      if (result.success) {
        onSave();
      } else {
        setError(result.error || 'Failed to add maintenance record');
      }
    } catch (err) {
      setError('Error adding record: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal show" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Add Maintenance Record</h3>
          <button className="close-btn" onClick={onClose}>
            <span className="material-icons-round">close</span>
          </button>
        </div>

        <div className="modal-body">
          <div className="form-grid">
            <div className="md-text-field">
              <label>Asset *</label>
              <select
                name="assetId"
                value={formData.assetId}
                onChange={handleChange}
                required
                disabled={!!assetId}
              >
                <option value="">Select asset...</option>
                {assets.map(asset => (
                  <option key={asset.assetId} value={asset.assetId}>
                    {asset.name} ({asset.assetId})
                  </option>
                ))}
              </select>
            </div>

            <div className="md-text-field">
              <label>Performed By *</label>
              <input
                name="performedBy"
                value={formData.performedBy}
                onChange={handleChange}
                required
              />
            </div>

            <div className="md-text-field">
              <label>Date *</label>
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
                required
              />
            </div>

            <div className="md-text-field">
              <label>Status</label>
              <select name="status" value={formData.status} onChange={handleChange}>
                <option value="completed">Completed</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
              </select>
            </div>

            <div className="md-text-field" style={{ gridColumn: '1 / -1' }}>
              <label>Description *</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows={4}
                required
              />
            </div>
          </div>

          {error && (
            <div className="error-message" style={{ marginTop: 16, color: '#f44336' }}>
              {error}
            </div>
          )}
        </div>

        <div className="modal-actions">
          <button className="md-button outline" onClick={onClose}>
            Cancel
          </button>
          <button className="md-button" onClick={handleSubmit} disabled={loading}>
            {loading ? 'Saving...' : 'Save Record'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default MaintenanceForm;
