import React, { useState, useEffect, useRef } from 'react';
import './MaintenanceTab.css';

const MaintenanceTab = ({ user }) => {
  const [view, setView] = useState('scanner'); // 'scanner' or 'form'
  const [scannedAsset, setScannedAsset] = useState(null);
  const [scanning, setScanning] = useState(false);
  const [maintenanceRecords, setMaintenanceRecords] = useState([]);
  const [formData, setFormData] = useState({
    maintenanceType: 'Preventive',
    description: '',
    status: 'completed',
    date: new Date().toISOString().split('T')[0]
  });
  
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const scanIntervalRef = useRef(null);

  useEffect(() => {
    loadMaintenanceRecords();
  }, []);

  useEffect(() => {
    return () => {
      stopCamera();
    };
  }, []);

  const loadMaintenanceRecords = async () => {
    try {
      const response = await fetch('/api/maintenance', { credentials: 'include' });
      const data = await response.json();
      if (data.success) {
        setMaintenanceRecords(data.records || []);
      }
    } catch (error) {
      console.error('Failed to load maintenance records:', error);
    }
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment', width: 1280, height: 720 }
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        streamRef.current = stream;
        setScanning(true);
        
        // Start scanning frames
        scanIntervalRef.current = setInterval(scanFrame, 500);
      }
    } catch (error) {
      console.error('Camera access error:', error);
      alert('Unable to access camera. Please grant camera permissions.');
    }
  };

  const stopCamera = () => {
    if (scanIntervalRef.current) {
      clearInterval(scanIntervalRef.current);
      scanIntervalRef.current = null;
    }
    
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    
    setScanning(false);
  };

  const scanFrame = async () => {
    if (!videoRef.current || !canvasRef.current || videoRef.current.readyState !== 4) {
      return;
    }

    const canvas = canvasRef.current;
    const video = videoRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    try {
      const imageData = canvas.toDataURL('image/jpeg', 0.8);
      
      const response = await fetch('/api/scan-qr-frame', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ image: imageData })
      });

      const result = await response.json();
      
      if (result.success && result.asset) {
        // QR detected! Stop scanning and open form
        stopCamera();
        setScannedAsset(result.asset);
        setView('form');
        
        // Optional: Play success sound or vibration
        if (navigator.vibrate) {
          navigator.vibrate(200);
        }
      }
    } catch (error) {
      console.error('QR scan error:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmitMaintenance = async (e) => {
    e.preventDefault();
    
    if (!scannedAsset) {
      alert('Please scan a QR code first');
      return;
    }

    try {
      const payload = {
        assetId: scannedAsset.asset_id || scannedAsset.assetId,
        maintenanceType: formData.maintenanceType,
        description: formData.description,
        status: formData.status,
        date: formData.date,
        performedBy: user?.username || 'Unknown'
      };

      const response = await fetch('/api/maintenance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(payload)
      });

      const result = await response.json();
      
      if (result.success) {
        alert('Maintenance record saved successfully!');
        
        // Reset and go back to scanner
        setFormData({
          maintenanceType: 'Preventive',
          description: '',
          status: 'completed',
          date: new Date().toISOString().split('T')[0]
        });
        setScannedAsset(null);
        setView('scanner');
        loadMaintenanceRecords();
      } else {
        alert('Error: ' + (result.error || 'Failed to save'));
      }
    } catch (error) {
      console.error('Maintenance submission error:', error);
      alert('Error submitting maintenance record');
    }
  };

  const cancelForm = () => {
    setScannedAsset(null);
    setView('scanner');
    setFormData({
      maintenanceType: 'Preventive',
      description: '',
      status: 'completed',
      date: new Date().toISOString().split('T')[0]
    });
  };

  return (
    <div className="maintenance-tab-container">
      <div className="page-header">
        <h2 className="page-title">Maintenance Records</h2>
        <p className="page-subtitle">Track and manage asset maintenance history</p>
      </div>

      {view === 'scanner' && (
        <div className="md-card scanner-card">
          <div className="card-header">
            <span className="material-icons-round card-icon">qr_code_scanner</span>
            <h3>Scan Asset QR Code</h3>
          </div>

          <div className="scanner-container">
            {!scanning ? (
              <div className="scanner-placeholder">
                <span className="material-icons-round">qr_code_scanner</span>
                <p>Click the button below to start scanning</p>
                <button onClick={startCamera} className="md-button">
                  <span className="material-icons-round">camera</span>
                  Start Camera
                </button>
              </div>
            ) : (
              <div className="video-container">
                <video ref={videoRef} autoPlay playsInline />
                <canvas ref={canvasRef} style={{ display: 'none' }} />
                <div className="scanner-overlay">
                  <div className="scan-frame"></div>
                  <p className="scan-hint">Position QR code within frame</p>
                </div>
                <button onClick={stopCamera} className="stop-camera-btn">
                  <span className="material-icons-round">close</span>
                  Stop Camera
                </button>
              </div>
            )}
          </div>
        </div>
      )}

      {view === 'form' && scannedAsset && (
        <div className="md-card maintenance-form-card">
          <div className="card-header">
            <span className="material-icons-round card-icon">build</span>
            <h3>Add Maintenance Record</h3>
          </div>

          {/* Scanned Asset Info */}
          <div className="scanned-asset-info">
            <h4>Scanned Asset</h4>
            <div className="asset-detail-row">
              <span className="detail-label">Asset ID:</span>
              <span className="detail-value">{scannedAsset.asset_id || scannedAsset.assetId}</span>
            </div>
            <div className="asset-detail-row">
              <span className="detail-label">Type:</span>
              <span className="detail-value">{scannedAsset.type}</span>
            </div>
            <div className="asset-detail-row">
              <span className="detail-label">Manufacturer:</span>
              <span className="detail-value">{scannedAsset.manufacturer || scannedAsset.manufacturer_id}</span>
            </div>
          </div>

          {/* Maintenance Form */}
          <form onSubmit={handleSubmitMaintenance} className="maintenance-form">
            <div className="md-text-field">
              <label>Maintenance Type</label>
              <select
                name="maintenanceType"
                value={formData.maintenanceType}
                onChange={handleInputChange}
                required
              >
                <option value="Preventive">Preventive</option>
                <option value="Corrective">Corrective</option>
                <option value="Inspection">Inspection</option>
                <option value="Emergency">Emergency</option>
              </select>
            </div>

            <div className="md-text-field">
              <label>Date</label>
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="md-text-field">
              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="Enter maintenance details..."
                rows="4"
                required
              />
            </div>

            <div className="md-text-field">
              <label>Status</label>
              <select
                name="status"
                value={formData.status}
                onChange={handleInputChange}
                required
              >
                <option value="completed">Completed</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
              </select>
            </div>

            <div className="form-actions">
              <button type="submit" className="md-button">
                <span className="material-icons-round">save</span>
                Save Record
              </button>
              <button type="button" onClick={cancelForm} className="md-button secondary">
                <span className="material-icons-round">close</span>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Maintenance Records Table */}
      <div className="md-card records-card">
        <div className="card-header">
          <span className="material-icons-round card-icon">history</span>
          <h3>Recent Maintenance Records</h3>
        </div>

        <div className="table-container">
          <table className="md-table">
            <thead>
              <tr>
                <th>Asset ID</th>
                <th>Date</th>
                <th>Type</th>
                <th>Operator</th>
                <th>Description</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {maintenanceRecords.length === 0 ? (
                <tr>
                  <td colSpan="6" className="empty-state">
                    No maintenance records found
                  </td>
                </tr>
              ) : (
                maintenanceRecords.map((record, index) => (
                  <tr key={index}>
                    <td>{record.asset_id}</td>
                    <td>{record.date}</td>
                    <td>{record.maintenance_type}</td>
                    <td>{record.operator}</td>
                    <td className="description-cell">{record.description}</td>
                    <td>
                      <span className={`md-badge ${record.status === 'completed' ? 'success' : 'warning'}`}>
                        {record.status}
                      </span>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default MaintenanceTab;
