import React, { useState } from 'react';
import './RegistrationModal.css';
import api from '../services/api';
const RegistrationModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    email: '',
    role: 'manufacturer'
  });
  const [message, setMessage] = useState({ text: '', type: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      const result = await api.auth.register(formData);

      if (result.success) {
        setMessage({ text: 'Registration submitted. Await admin approval.', type: 'success' });
        setTimeout(() => {
          onClose();
          setFormData({
            username: '',
            password: '',
            name: '',
            email: '',
            role: 'manufacturer'
          });
          setMessage({ text: '', type: '' });
        }, 2000);
      } else {
        setMessage({ text: result.error || 'Registration failed', type: 'error' });
      }
    } catch (error) {
      setMessage({ text: 'Registration error: ' + error.message, type: 'error' });
    }
  };

  if (!isOpen) return null;

  return (
    <div className={`modal ${isOpen ? 'show' : ''}`} onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <span className="material-icons-round">person_add</span>
          <h3>Register New Account</h3>
        </div>

        <div className="form-grid">
          <div className="md-text-field">
            <label>Username</label>
            <input
              name="username"
              value={formData.username}
              onChange={handleInputChange}
            />
          </div>

          <div className="md-text-field">
            <label>Password</label>
            <input
              name="password"
              type="password"
              value={formData.password}
              onChange={handleInputChange}
            />
          </div>

          <div className="md-text-field">
            <label>Name</label>
            <input
              name="name"
              value={formData.name}
              onChange={handleInputChange}
            />
          </div>

          <div className="md-text-field">
            <label>Email</label>
            <input
              name="email"
              type="email"
              value={formData.email}
              onChange={handleInputChange}
            />
          </div>

          <div className="md-text-field">
            <label>Role</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleInputChange}
            >
              <option value="manufacturer">Manufacturer</option>
              <option value="worker">Worker</option>
              <option value="engineer">Engineer</option>
            </select>
          </div>
        </div>

        {message.text && (
          <div style={{ 
            marginTop: '16px', 
            padding: '12px', 
            borderRadius: '8px',
            backgroundColor: message.type === 'success' ? '#4caf50' : '#f44336',
            color: 'white'
          }}>
            {message.text}
          </div>
        )}

        <div className="modal-actions">
          <button className="md-button outline" onClick={onClose}>
            Cancel
          </button>
          <button className="md-button" onClick={handleSubmit}>
            Submit
          </button>
        </div>
      </div>
    </div>
  );
};

export default RegistrationModal;
