import React, { useEffect, useState } from 'react';
import PendingUsersTable from './PendingUsersTable';
import api from '../../services/api';

const AdminApprovals = () => {
  const [pendingUsers, setPendingUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchPendingUsers = async () => {
    setLoading(true);
    try {
      const data = await api.admin.getPendingUsers();
      setPendingUsers(data.users || []);
    } catch (error) {
      console.error('Failed to fetch pending users:', error);
      setPendingUsers([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPendingUsers();
  }, []);

  const handleApprovalUpdate = () => {
    // Refresh the list after approval/rejection
    fetchPendingUsers();
  };

  return (
    <div className="admin-approvals">
      <h2>User Approval Management</h2>
      <p>Review and approve pending user registrations</p>
      
      {loading ? (
        <div className="loading-spinner">Loading pending users...</div>
      ) : pendingUsers.length === 0 ? (
        <div className="empty-state">
          <span className="material-icons-round">check_circle</span>
          <p>No pending approvals</p>
        </div>
      ) : (
        <PendingUsersTable 
          users={pendingUsers} 
          onUpdate={handleApprovalUpdate}
        />
      )}
    </div>
  );
};

export default AdminApprovals;
