import React from 'react';
import './QRGenerator.css';

const QRActions = ({ data, options }) => {
  const handleDownload = async () => {
    if (options.format === 'svg') {
      QRCode.toString(data, {
        type: 'svg',
        errorCorrectionLevel: options.errorCorrection,
        width: 800,
        color: {
          dark: '#1976D2',
          light: '#FFFFFF'
        }
      })
        .then(svgString => {
          const blob = new Blob([svgString], { type: 'image/svg+xml' });
          const url = URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = `asset-qr-${Date.now()}.svg`;
          a.click();
          URL.revokeObjectURL(url);
        })
        .catch(err => console.error('SVG generation error:', err));
    } else {
      QRCode.toDataURL(data, {
        errorCorrectionLevel: options.errorCorrection,
        width: 800,
        margin: 2,
        color: {
          dark: '#1976D2',
          light: '#FFFFFF'
        }
      })
        .then(url => {
          const a = document.createElement('a');
          a.href = url;
          a.download = `asset-qr-${Date.now()}.png`;
          a.click();
        })
        .catch(err => console.error('PNG generation error:', err));
    }
  };

  const handleSendToLaser = () => {
    alert('QR code sent to laser marking system (feature stub).');
  };

  return (
    <div className="qr-actions">
      <button onClick={handleDownload} className="md-button">
        <span className="material-icons-round">download</span>
        Download QR Code
      </button>
      <button onClick={handleSendToLaser} className="md-button secondary">
        <span className="material-icons-round">flash_on</span>
        Send to Laser
      </button>
    </div>
  );
};

export default QRActions;
