import React, { useState, useEffect } from 'react';
import Header from './Header';
import Navbar from './Navbar';
import AssetList from './Assets/AssetList';
import QRGenerator from './QRGenerator/QRGenerator';
import ReportsTab from './Reports/ReportsTab';
import AdminApprovals from './Admin/AdminApprovals';
import MaintenanceTab from './Maintenance/MaintenanceTab';

// In the render section, update the maintenance tab:
{activeTab === 'maintenance' && <MaintenanceTab user={user} />}

const Dashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('assets');
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Role-based permissions
  const PERMISSIONS = {
    admin: {
      tabs: ['assets', 'qr-generator', 'maintenance', 'reports', 'admin-approvals'],
      canAddAsset: true,
      canDeleteAsset: true,
      canGenerateQR: true
    },
    manufacturer: {
      tabs: ['assets', 'qr-generator'],
      canAddAsset: true,
      canDeleteAsset: false,
      canGenerateQR: true
    },
    worker: {
      tabs: ['maintenance'],
      canAddAsset: false,
      canDeleteAsset: false,
      canGenerateQR: false
    },
    engineer: {
      tabs: ['maintenance', 'assets', 'reports'],
      canAddAsset: false,
      canDeleteAsset: false,
      canGenerateQR: false
    }
  };

  const userPermissions = PERMISSIONS[user.role] || PERMISSIONS.worker;

  useEffect(() => {
    // Set first allowed tab as active
    if (userPermissions.tabs.length > 0) {
      setActiveTab(userPermissions.tabs[0]);
    }
  }, [user.role]);

  const handleTabChange = (tabId) => {
    if (userPermissions.tabs.includes(tabId)) {
      setActiveTab(tabId);
      setMobileMenuOpen(false);
    }
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'assets':
        return <AssetList permissions={userPermissions} />;
      case 'qr-generator':
        return <QRGenerator />;
      case 'maintenance':
        return <MaintenanceTab user={user} />;
      case 'reports':
        return <ReportsTab />;
      case 'admin-approvals':
        return <AdminApprovals />;
      default:
        return <AssetList permissions={userPermissions} />;
    }
  };

  return (
    <div id="mainApp">
      <Header 
        user={user} 
        onLogout={onLogout}
        mobileMenuOpen={mobileMenuOpen}
        toggleMobileMenu={toggleMobileMenu}
      />
      
      <Navbar
        activeTab={activeTab}
        onTabChange={handleTabChange}
        allowedTabs={userPermissions.tabs}
        mobileMenuOpen={mobileMenuOpen}
      />

      <main className="main-content">
        <div className="content-section">
          {renderTabContent()}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
