import React from 'react';

const QRConfiguration = ({ assetList, selectedAsset, setSelectedAsset, qrOptions, setQrOptions }) => {
  const handleAssetChange = (e) => {
    const assetId = parseInt(e.target.value, 10);
    const asset = assetList.find(a => a.assetId === assetId);
    setSelectedAsset(asset || null);
  };

  const handleOptionChange = (e) => {
    const { name, value } = e.target;
    setQrOptions(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="qr-config-form">
      <div className="md-text-field">
        <label>Select Asset *</label>
        <select value={selectedAsset ? selectedAsset.assetId : ''} onChange={handleAssetChange} required>
          <option value="" disabled>Select asset...</option>
          {assetList.map(asset => (
            <option key={asset.assetId} value={asset.assetId}>
              {asset.name} ({asset.assetId})
            </option>
          ))}
        </select>
      </div>
      <div className="md-text-field">
        <label>Error Correction</label>
        <select name="errorCorrection" value={qrOptions.errorCorrection} onChange={handleOptionChange}>
          <option value="L">Low (L)</option>
          <option value="M">Medium (M)</option>
          <option value="Q">Quartile (Q)</option>
          <option value="H">High (H)</option>
        </select>
      </div>
      <div className="md-text-field">
        <label>Format</label>
        <select name="format" value={qrOptions.format} onChange={handleOptionChange}>
          <option value="svg">SVG</option>
          <option value="png">PNG</option>
        </select>
      </div>
    </div>
  );
};

export default QRConfiguration;
