import React, { useState } from 'react';
import './RegistrationModal.css';
import api from '../services/api';

const RegistrationModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    email: '',
    role: 'manufacturer'
  });

  const [message, setMessage] = useState({ text: '', type: '' });
  const [otpSent, setOtpSent] = useState(false);
  const [otpInput, setOtpInput] = useState('');
  const [otpVerified, setOtpVerified] = useState(false);
  const [sendingOtp, setSendingOtp] = useState(false);
  const [verifyingOtp, setVerifyingOtp] = useState(false);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleOtpInputChange = (e) => {
    setOtpInput(e.target.value);
  };

  const handleSendOtp = async () => {
    if (!formData.email) {
      setMessage({ text: 'Please enter an email address first', type: 'error' });
      return;
    }

    try {
      setSendingOtp(true);
      const res = await api.auth.sendOtp(formData.email);
      if (res.success) {
        setOtpSent(true);
        setMessage({ text: 'OTP sent to your email. Enter it below to verify.', type: 'success' });
      } else {
        setMessage({ text: res.error || 'Failed to send OTP', type: 'error' });
      }
    } catch (err) {
      setMessage({ text: 'Error sending OTP: ' + err.message, type: 'error' });
    } finally {
      setSendingOtp(false);
    }
  };

  const handleVerifyOtp = async () => {
    if (!otpInput || !formData.email) {
      setMessage({ text: 'Please enter the OTP and your email', type: 'error' });
      return;
    }

    try {
      setVerifyingOtp(true);
      const res = await api.auth.verifyOtp(formData.email, otpInput);
      if (res.success) {
        setOtpVerified(true);
        setMessage({ text: 'Email verified. You can now submit the registration.', type: 'success' });
      } else {
        setMessage({ text: res.error || 'Invalid OTP', type: 'error' });
      }
    } catch (err) {
      setMessage({ text: 'Error verifying OTP: ' + err.message, type: 'error' });
    } finally {
      setVerifyingOtp(false);
    }
  };

  const handleSubmit = async () => {
    try {
      const result = await api.auth.register(formData);

      if (result.success) {
        // ✅ Show success message modal
        setShowSuccessMessage(true);

        // Auto-hide after 10 seconds
        setTimeout(() => {
          setShowSuccessMessage(false);
        }, 10000);

        setMessage({ text: 'Registration submitted. Await admin approval.', type: 'success' });

        // Close modal and reset after 2 seconds
        setTimeout(() => {
          onClose();
          setFormData({
            username: '',
            password: '',
            name: '',
            email: '',
            role: 'manufacturer'
          });
          setMessage({ text: '', type: '' });
          setOtpSent(false);
          setOtpInput('');
          setOtpVerified(false);
        }, 2000);
      } else {
        setMessage({ text: result.error || 'Registration failed', type: 'error' });
      }
    } catch (error) {
      setMessage({ text: 'Registration error: ' + error.message, type: 'error' });
    }
  };

  if (!isOpen) return null;

  return (
    <>
      {/* ✅ SUCCESS MESSAGE MODAL - Fixed positioning */}
      {showSuccessMessage && (
        <div className="success-alert">
          <div className="alert-content">
            <h3>✅ Registration Submitted Successfully!</h3>
            <p>
              Your registration request has been sent to the admin for approval.
            </p>
            <p>
              <strong>Any updates will be sent to you via email, so stay tuned!</strong>
            </p>
            <p className="small-text">
              Please check your email (including spam folder) for notifications.
            </p>
            <button 
              className="close-btn" 
              onClick={() => setShowSuccessMessage(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* REGISTRATION MODAL */}
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <button className="modal-close" onClick={onClose}>×</button>
          <h2>Register</h2>

          {message.text && (
            <div className={`message ${message.type}`}>
              {message.text}
            </div>
          )}

          <form>
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Email</label>
              <div style={{ display: 'flex', gap: '10px' }}>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  disabled={otpVerified}
                  required
                  style={{ flex: 1 }}
                />
                {!otpVerified && (
                  <button
                    type="button"
                    onClick={handleSendOtp}
                    disabled={sendingOtp || otpSent}
                    className="otp-button"
                  >
                    {sendingOtp ? 'Sending...' : otpSent ? 'Sent' : 'Send OTP'}
                  </button>
                )}
                {otpVerified && (
                  <span style={{ color: 'green', alignSelf: 'center' }}>✓ Verified</span>
                )}
              </div>
            </div>

            {otpSent && !otpVerified && (
              <div className="form-group">
                <label>Enter OTP</label>
                <div style={{ display: 'flex', gap: '10px' }}>
                  <input
                    type="text"
                    value={otpInput}
                    onChange={handleOtpInputChange}
                    placeholder="Enter OTP"
                    style={{ flex: 1 }}
                  />
                  <button
                    type="button"
                    onClick={handleVerifyOtp}
                    disabled={verifyingOtp}
                    className="otp-button"
                  >
                    {verifyingOtp ? 'Verifying...' : 'Verify'}
                  </button>
                </div>
              </div>
            )}

            <div className="form-group">
              <label>Role</label>
              <select
                name="role"
                value={formData.role}
                onChange={handleInputChange}
                required
              >
                <option value="manufacturer">Manufacturer</option>
                <option value="engineer">Engineer</option>
                <option value="worker">Worker</option>
              </select>
            </div>

            <button
              type="button"
              onClick={handleSubmit}
              disabled={!otpVerified}
              className="submit-button"
            >
              Submit Registration
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default RegistrationModal;
