import React from 'react';
import AssetActions from './AssetActions';

const formatDate = (dateString) => {
  if (!dateString) return '';
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
};

const getStatusColor = (status) => {
  switch (status?.toLowerCase()) {
    case 'active':
      return 'success';
    case 'maintenance':
      return 'warning';
    case 'retired':
      return 'error';
    case 'installed':
      return 'info';
    default:
      return 'default';
  }
};

const AssetTable = ({ assets, onEdit, canDelete }) => {
  return (
    <table className="asset-table">
      <thead>
        <tr>
          <th>Asset ID</th>
          <th>Type</th>
          <th>Manufacturer</th>
          <th>Mfg Date</th>
          <th>Status</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {assets.length > 0 ? assets.map(asset => {
          const assetId = asset.assetId || asset.asset_id;
          return (
            <tr key={assetId}>
              <td>{assetId}</td>
              <td>{asset.type}</td>
              <td>{asset.manufacturer}</td>
              <td>{formatDate(asset.mfgDate || asset.mfg_date)}</td>
              <td><span className={`md-badge ${getStatusColor(asset.status)}`}>{asset.status}</span></td>
              <td><span className={`md-badge ${asset.hasQR ? 'success' : 'error'}`}>{asset.hasQR ? 'Generated' : 'Not Generated'}</span></td>
              <td>
                <AssetActions asset={asset} onEdit={onEdit} canDelete={canDelete} />
              </td>
            </tr>
          );
        }) : (
          <tr>
            <td colSpan="6" style={{ textAlign: 'center', padding: '40px' }}>
              <div className="empty-state">
                <span className="material-icons-round" style={{ fontSize: '48px', color: '#999', marginBottom: '12px' }}>inventory_2</span>
                <p>No assets found.</p>
              </div>
            </td>
          </tr>
        )}
      </tbody>
    </table>
  );
};

export default AssetTable;
