import React, { useState, useEffect } from 'react';
import QRScanner from './QRScanner';
import api from '../../services/api';
import './MaintenanceTab.css';

const MaintenanceTab = ({ user }) => {
  const [view, setView] = useState('scanner'); // 'scanner' or 'form'
  const [scannedAsset, setScannedAsset] = useState(null);
  const [showScanner, setShowScanner] = useState(false);
  const [maintenanceRecords, setMaintenanceRecords] = useState([]);
  const [formData, setFormData] = useState({
    maintenanceType: 'Preventive',
    description: '',
    status: 'completed',
    date: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    loadMaintenanceRecords();
  }, []);

  const loadMaintenanceRecords = async () => {
    try {
      const data = await api.maintenance.getAll();
      if (data.success) {
        setMaintenanceRecords(data.records || []);
      }
    } catch (error) {
      console.error('Failed to load maintenance records:', error);
    }
  };

  const handleStartScan = () => {
    setShowScanner(true);
  };

  const handleScanComplete = async (assetId) => {
    try {
      // Close the scanner first
      setShowScanner(false);
      
      // Create the initial form state with the scanned asset ID
      setFormData(prev => ({
        ...prev,
        assetId: assetId,
        maintenanceType: 'Routine Maintenance',
        date: new Date().toISOString().split('T')[0],
        description: '',
        operator: user?.username || ''
      }));
      
      // Switch to form view
      setView('form');
      
      // Store the asset ID for form submission
      setScannedAsset({ asset_id: assetId });
      
    } catch (error) {
      console.error('Error handling QR scan:', error);
      alert('Error: Unable to process QR code');
    }
  };



  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmitMaintenance = async (e) => {
    e.preventDefault();
    
    if (!scannedAsset) {
      alert('Please scan a QR code first');
      return;
    }

    try {
      const payload = {
        assetId: scannedAsset.asset_id || scannedAsset.assetId,
        maintenanceType: formData.maintenanceType,
        description: formData.description,
        status: formData.status,
        date: formData.date,
        performedBy: user?.username || 'Unknown'
      };

      const result = await api.maintenance.create(payload);
      
      if (result.success) {
        alert('Maintenance record saved successfully!');
        
        // Reset and go back to scanner
        setFormData({
          maintenanceType: 'Preventive',
          description: '',
          status: 'completed',
          date: new Date().toISOString().split('T')[0]
        });
        setScannedAsset(null);
        setView('scanner');
        loadMaintenanceRecords();
      } else {
        alert('Error: ' + (result.error || 'Failed to save'));
      }
    } catch (error) {
      console.error('Maintenance submission error:', error);
      alert('Error submitting maintenance record');
    }
  };

  const cancelForm = () => {
    setScannedAsset(null);
    setView('scanner');
    setFormData({
      maintenanceType: 'Preventive',
      description: '',
      status: 'completed',
      date: new Date().toISOString().split('T')[0]
    });
  };

  return (
    <div className="maintenance-tab-container">
      <div className="page-header">
        <h2 className="page-title">Maintenance Records</h2>
        <p className="page-subtitle">Track and manage asset maintenance history</p>
      </div>

      {view === 'scanner' && (
        <div className="md-card scanner-card">
          <div className="card-header">
            <span className="material-icons-round card-icon">qr_code_scanner</span>
            <h3>Scan Asset QR Code</h3>
          </div>

          <div className="scanner-container">
            <div className="scanner-placeholder">
              <span className="material-icons-round">qr_code_scanner</span>
              <p>Click the button below to start scanning</p>
              <button onClick={handleStartScan} className="md-button">
                <span className="material-icons-round">camera</span>
                Start Camera
              </button>
            </div>
          </div>
        </div>
      )}

      {/* QR Scanner Modal */}
      <QRScanner 
        isOpen={showScanner}
        onClose={() => setShowScanner(false)}
        onScanComplete={handleScanComplete}
      />

      {view === 'form' && scannedAsset && (
        <div className="md-card maintenance-form-card">
          <div className="card-header">
            <span className="material-icons-round card-icon">build</span>
            <h3>Add Maintenance Record</h3>
          </div>

          {/* Scanned Asset Info */}
          <div className="scanned-asset-info">
            <h4>Scanned Asset</h4>
            <div className="asset-detail-row">
              <span className="detail-label">Asset ID:</span>
              <span className="detail-value">{scannedAsset.asset_id || scannedAsset.assetId}</span>
            </div>
            <div className="asset-detail-row">
              <span className="detail-label">Type:</span>
              <span className="detail-value">{scannedAsset.type}</span>
            </div>
            <div className="asset-detail-row">
              <span className="detail-label">Manufacturer:</span>
              <span className="detail-value">{scannedAsset.manufacturer || scannedAsset.manufacturer_id}</span>
            </div>
          </div>

          {/* Maintenance Form */}
          <form onSubmit={handleSubmitMaintenance} className="maintenance-form">
            <div className="md-text-field">
              <label>Maintenance Type</label>
              <select
                name="maintenanceType"
                value={formData.maintenanceType}
                onChange={handleInputChange}
                required
              >
                <option value="Preventive">Preventive</option>
                <option value="Corrective">Corrective</option>
                <option value="Inspection">Inspection</option>
                <option value="Emergency">Emergency</option>
              </select>
            </div>

            <div className="md-text-field">
              <label>Date</label>
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="md-text-field">
              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="Enter maintenance details..."
                rows="4"
                required
              />
            </div>

            <div className="md-text-field">
              <label>Status</label>
              <select
                name="status"
                value={formData.status}
                onChange={handleInputChange}
                required
              >
                <option value="completed">Completed</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
              </select>
            </div>

            <div className="form-actions">
              <button type="submit" className="md-button">
                <span className="material-icons-round">save</span>
                Save Record
              </button>
              <button type="button" onClick={cancelForm} className="md-button secondary">
                <span className="material-icons-round">close</span>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Maintenance Records Table */}
      <div className="md-card records-card">
        <div className="card-header">
          <span className="material-icons-round card-icon">history</span>
          <h3>Recent Maintenance Records</h3>
        </div>

        <div className="table-container">
          <table className="md-table">
            <thead>
              <tr>
                <th>Asset ID</th>
                <th>Date</th>
                <th>Type</th>
                <th>Operator</th>
                <th>Description</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {maintenanceRecords.length === 0 ? (
                <tr>
                  <td colSpan="6" className="empty-state">
                    No maintenance records found
                  </td>
                </tr>
              ) : (
                maintenanceRecords.map((record, index) => (
                  <tr key={index}>
                    <td>{record.asset_id}</td>
                    <td>{record.date}</td>
                    <td>{record.maintenance_type}</td>
                    <td>{record.operator}</td>
                    <td className="description-cell">{record.description}</td>
                    <td>
                      <span className={`md-badge ${record.status === 'completed' ? 'success' : 'warning'}`}>
                        {record.status}
                      </span>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default MaintenanceTab;
