import React, { useState, useEffect, useRef } from 'react';
import QRScanner from './QRScanner';
import api from '../../services/api';
import './MaintenanceTab.css';

const MaintenanceTab = ({ user }) => {
  const [view, setView] = useState('scanner'); // 'scanner' or 'form'
  const [scannedAsset, setScannedAsset] = useState(null);
  const [showScanner, setShowScanner] = useState(false);
  const [maintenanceRecords, setMaintenanceRecords] = useState([]);
  const [formData, setFormData] = useState({
    asset_id: '',
    maintenanceType: 'General',
    operator: user?.username || '',
    description: '',
    status: 'completed',
    date: new Date().toISOString().split('T')[0]
  });

  // New states for AI enhancement and speech-to-text
  const [isListening, setIsListening] = useState(false);
  const [isEnhancing, setIsEnhancing] = useState(false);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  const recognitionRef = useRef(null);

  useEffect(() => {
    loadMaintenanceRecords();
    initializeSpeechRecognition();
    
    return () => {
      if (recognitionRef.current && isListening) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const initializeSpeechRecognition = () => {
    // Check browser support for Web Speech API
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    
    if (SpeechRecognition) {
      setSpeechSupported(true);
      
      // Initialize recognition
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'en-US';

      // Handle speech results
      recognitionRef.current.onresult = (event) => {
        let finalTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript + ' ';
          }
        }

        if (finalTranscript) {
          setFormData(prev => ({
            ...prev,
            description: prev.description + finalTranscript
          }));
        }
      };

      // Handle errors
      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };

      // Handle end
      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    }
  };

  const loadMaintenanceRecords = async () => {
    try {
      const data = await api.maintenance.getAll();
      if (data.success) {
        setMaintenanceRecords(data.records || []);
      }
    } catch (error) {
      console.error('Failed to load maintenance records:', error);
    }
  };

  const handleStartScan = () => {
    setShowScanner(true);
  };

  const handleScanComplete = async (assetId) => {
    try {
      console.log('Scan complete with asset ID:', assetId);
      setShowScanner(false);

      const response = await api.assets.getAll();
      const asset = response.assets.find(a => a.asset_id === assetId || a.assetId === assetId);

      if (!asset) {
        throw new Error('Asset not found');
      }

      setFormData(prev => ({
        ...prev,
        asset_id: asset.asset_id || asset.assetId,
        maintenanceType: 'General',
        date: new Date().toISOString().split('T')[0],
        description: '',
        status: 'completed',
        operator: user?.username || ''
      }));

      setScannedAsset(asset);
      setView('form');
      console.log('Successfully loaded asset and switched to form view');
    } catch (error) {
      console.error('Error handling QR scan:', error);
      alert('Error: Unable to find asset details. Please try scanning again.');
      setScannedAsset(null);
      setFormData(prev => ({
        ...prev,
        asset_id: '',
        description: ''
      }));
      setView('scanner');
    }
  };

  const toggleListening = () => {
    if (!speechSupported) {
      setError('Speech recognition is not supported in your browser. Please use Chrome, Edge, or Safari.');
      return;
    }

    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      setError('');
      setSuccess('');
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const handleEnhanceDescription = async () => {
    if (!formData.description.trim()) {
      setError('Please enter or speak a description first');
      return;
    }

    setIsEnhancing(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch('/api/ai/enhance-description', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ description: formData.description.trim() }),
      });

      const data = await response.json();

      if (data.success) {
        setFormData(prev => ({
          ...prev,
          description: data.enhanced_description
        }));
        setSuccess('Description enhanced successfully!');
        setTimeout(() => setSuccess(''), 3000);
      } else {
        if (data.error_type === 'OLLAMA_OFFLINE') {
          setError('AI model is offline. Please start Ollama and ensure Llama 3.2:3b is installed.');
        } else {
          setError(data.error || 'Failed to enhance description');
        }
      }
    } catch (err) {
      console.error('Enhancement error:', err);
      setError('Network error. Please check your connection.');
    } finally {
      setIsEnhancing(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear messages when user types
    if (name === 'description') {
      setError('');
      setSuccess('');
    }
  };

  const handleSubmitMaintenance = async (e) => {
    e.preventDefault();
    if (!scannedAsset) {
      alert('Please scan a QR code first');
      return;
    }

    try {
      const payload = {
        asset_id: scannedAsset.asset_id || scannedAsset.aid,
        maintenanceType: formData.maintenanceType,
        description: formData.description,
        status: formData.status,
        date: formData.date,
        performedBy: user?.username || 'Unknown'
      };

      const result = await api.maintenance.create(payload);

      if (result.success) {
        alert('Maintenance record saved successfully!');
        setFormData({
          asset_id: '',
          maintenanceType: 'General',
          operator: user?.username || '',
          description: '',
          status: 'completed',
          date: new Date().toISOString().split('T')[0]
        });
        setScannedAsset(null);
        setView('scanner');
        setError('');
        setSuccess('');
        loadMaintenanceRecords();
      } else {
        alert('Error: ' + (result.error || 'Failed to save'));
      }
    } catch (error) {
      console.error('Maintenance submission error:', error);
      alert('Error submitting maintenance record');
    }
  };

  const cancelForm = () => {
    setScannedAsset(null);
    setView('scanner');
    setFormData({
      asset_id: '',
      maintenanceType: 'General',
      operator: user?.username || '',
      description: '',
      status: 'completed',
      date: new Date().toISOString().split('T')[0]
    });
    setError('');
    setSuccess('');
    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  return (
    <div className="maintenance-tab">
      <div className="tab-header">
        <h2>Maintenance Management</h2>
        <p>Track and manage asset maintenance history</p>
      </div>

      {view === 'scanner' && (
        <div className="scanner-view">
          <div className="scan-prompt">
            <div className="scan-icon">üì±</div>
            <h3>Scan Asset QR Code</h3>
            <p>Position the QR code within the camera frame</p>
            <button className="scan-button" onClick={handleStartScan}>
              Start Scanning
            </button>
          </div>

          {showScanner && (
            <div className="scanner-modal">
              <QRScanner
                onScanComplete={handleScanComplete}
                onClose={() => setShowScanner(false)}
              />
            </div>
          )}

          <div className="records-section">
            <h3>Recent Maintenance Records</h3>
            <div className="records-table-container">
              <table className="records-table">
                <thead>
                  <tr>
                    <th>Asset ID</th>
                    <th>Date</th>
                    <th>Type</th>
                    <th>Operator</th>
                    <th>Description</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {maintenanceRecords.length === 0 ? (
                    <tr>
                      <td colSpan="6" className="no-records">
                        No maintenance records found
                      </td>
                    </tr>
                  ) : (
                    maintenanceRecords.map((record, index) => (
                      <tr key={index}>
                        <td>{record.asset_id}</td>
                        <td>{record.date}</td>
                        <td>{record.maintenance_type}</td>
                        <td>{record.operator}</td>
                        <td>{record.description}</td>
                        <td>
                          <span className={`status-badge ${record.status}`}>
                            {record.status}
                          </span>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {view === 'form' && scannedAsset && (
        <div className="form-view">
          <div className="asset-info-card">
            <h3>Asset Information</h3>
            <div className="asset-details">
              <div className="detail-item">
                <span className="label">Asset ID:</span>
                <span className="value">{scannedAsset.asset_id || scannedAsset.aid}</span>
              </div>
              <div className="detail-item">
                <span className="label">Type:</span>
                <span className="value">{scannedAsset.type}</span>
              </div>
              <div className="detail-item">
                <span className="label">Location:</span>
                <span className="value">{scannedAsset.location}</span>
              </div>
            </div>
          </div>

          <form className="maintenance-form" onSubmit={handleSubmitMaintenance}>
            <div className="form-group">
              <label htmlFor="maintenanceType">Maintenance Type</label>
              <select
                id="maintenanceType"
                name="maintenanceType"
                value={formData.maintenanceType}
                onChange={handleInputChange}
                required
              >
                <option value="General">General</option>
                <option value="Preventive">Preventive</option>
                <option value="Corrective">Corrective</option>
                <option value="Emergency">Emergency</option>
                <option value="Inspection">Inspection</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="date">Date</label>
              <input
                type="date"
                id="date"
                name="date"
                value={formData.date}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="operator">Operator</label>
              <input
                type="text"
                id="operator"
                name="operator"
                value={formData.operator}
                onChange={handleInputChange}
                placeholder="Operator name"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="status">Status</label>
              <select
                id="status"
                name="status"
                value={formData.status}
                onChange={handleInputChange}
                required
              >
                <option value="completed">Completed</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
              </select>
            </div>

            {/* Smart Maintenance Description Section */}
            <div className="form-group description-group">
              <div className="description-header">
                <label htmlFor="description">Maintenance Description</label>
                <div className="ai-button-group">
                  {speechSupported && (
                    <button
                      type="button"
                      onClick={toggleListening}
                      className={`mic-button ${isListening ? 'listening' : ''}`}
                      title={isListening ? 'Stop recording' : 'Start voice input'}
                      disabled={isEnhancing}
                    >
                      <span className="mic-icon">{isListening ? '‚è∏Ô∏è' : 'üé§'}</span>
                      {isListening ? 'Stop' : 'Voice'}
                    </button>
                  )}
                  <button
                    type="button"
                    onClick={handleEnhanceDescription}
                    disabled={isEnhancing || !formData.description.trim()}
                    className="enhance-button"
                    title="Improve with AI"
                  >
                    <span className="ai-icon">‚ú®</span>
                    {isEnhancing ? 'Enhancing...' : 'Improve with AI'}
                  </button>
                </div>
              </div>

              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="Describe the maintenance work performed... (You can type or use voice input)"
                rows="6"
                className={isListening ? 'listening-active' : ''}
                disabled={isEnhancing}
                required
              />

              {isListening && (
                <div className="listening-indicator">
                  <span className="pulse"></span>
                  Listening... Speak now
                </div>
              )}

              {error && (
                <div className="error-message">
                  <span className="error-icon">‚ö†Ô∏è</span>
                  {error}
                </div>
              )}

              {success && (
                <div className="success-message">
                  <span className="success-icon">‚úì</span>
                  {success}
                </div>
              )}
            </div>

            <div className="form-actions">
              <button type="button" className="cancel-button" onClick={cancelForm}>
                Cancel
              </button>
              <button type="submit" className="submit-button">
                Submit Maintenance Record
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default MaintenanceTab;
