import React, { useState, useEffect, useRef } from 'react';
import './MaintenanceInput.css';

const MaintenanceInput = ({ onSubmit, initialValue = '' }) => {
  const [description, setDescription] = useState(initialValue);
  const [isListening, setIsListening] = useState(false);
  const [isEnhancing, setIsEnhancing] = useState(false);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  const recognitionRef = useRef(null);

  // NEW: Sync with parent's initialValue changes
  useEffect(() => {
    setDescription(initialValue);
  }, [initialValue]);

  // NEW: Notify parent of description changes
  useEffect(() => {
    if (onSubmit) {
      onSubmit(description);
    }
  }, [description]);

  useEffect(() => {
    // Check browser support for Web Speech API
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    
    if (SpeechRecognition) {
      setSpeechSupported(true);
      
      // Initialize recognition
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'en-US';

      // Handle speech results
      recognitionRef.current.onresult = (event) => {
        let interimTranscript = '';
        let finalTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript + ' ';
          } else {
            interimTranscript += transcript;
          }
        }

        if (finalTranscript) {
          setDescription(prev => prev + finalTranscript);
        }
      };

      // Handle errors
      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };

      // Handle end
      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    }

    return () => {
      if (recognitionRef.current && isListening) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const toggleListening = () => {
    if (!speechSupported) {
      setError('Speech recognition is not supported in your browser. Please use Chrome, Edge, or Safari.');
      return;
    }

    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      setError('');
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const handleEnhance = async () => {
    if (!description.trim()) {
      setError('Please enter or speak a description first');
      return;
    }

    setIsEnhancing(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch('/api/ai/enhance-description', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ description: description.trim() }),
      });

      const data = await response.json();

      if (data.success) {
        setDescription(data.enhanced_description);
        setSuccess('Description enhanced successfully!');
        setTimeout(() => setSuccess(''), 3000);
      } else {
        if (data.error_type === 'OLLAMA_OFFLINE') {
          setError('AI model is offline. Please start Ollama and ensure Llama 3.2:3b is installed.');
        } else {
          setError(data.error || 'Failed to enhance description');
        }
      }
    } catch (err) {
      console.error('Enhancement error:', err);
      setError('Network error. Please check your connection.');
    } finally {
      setIsEnhancing(false);
    }
  };

  return (
    <div className="maintenance-input-container">
      <div className="input-header">
        <label htmlFor="maintenance-description">Maintenance Description</label>
        <div className="button-group">
          {speechSupported && (
            <button
              type="button"
              onClick={toggleListening}
              className={`mic-button ${isListening ? 'listening' : ''}`}
              title={isListening ? 'Stop recording' : 'Start voice input'}
            >
              <span className="mic-icon">{isListening ? '‚è∏Ô∏è' : 'üé§'}</span>
              {isListening ? 'Stop' : 'Voice Input'}
            </button>
          )}
          <button
            type="button"
            onClick={handleEnhance}
            disabled={isEnhancing || !description.trim()}
            className="enhance-button"
            title="Improve with AI"
          >
            <span className="ai-icon">‚ú®</span>
            {isEnhancing ? 'Enhancing...' : 'Improve with AI'}
          </button>
        </div>
      </div>

      <textarea
        id="maintenance-description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Describe the maintenance work performed... (You can type or use voice input)"
        rows="6"
        className={isListening ? 'listening-active' : ''}
        disabled={isEnhancing}
        required
      />

      {isListening && (
        <div className="listening-indicator">
          <span className="pulse"></span>
          Listening... Speak now
        </div>
      )}

      {error && (
        <div className="error-message">
          <span className="error-icon">‚ö†Ô∏è</span>
          {error}
        </div>
      )}

      {success && (
        <div className="success-message">
          <span className="success-icon">‚úì</span>
          {success}
        </div>
      )}
    </div>
  );
};

export default MaintenanceInput;
