import React, { useState, useEffect } from 'react';
import MaintenanceTable from './MaintenanceTable';
import MaintenanceMobileCard from './MaintenanceMobileCard';
import MaintenanceForm from './MaintenanceForm';
import QRScanner from './QRScanner';
import QRFileUpload from './QRFileUpload';
import api from '../../services/api';

const MaintenanceTab = ({ user }) => {
  const [records, setRecords] = useState([]);
  const [showScanner, setShowScanner] = useState(false);
  const [showFileUpload, setShowFileUpload] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [selectedAssetId, setSelectedAssetId] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchRecords = async () => {
    setLoading(true);
    try {
      const data = await api.maintenance.getAll();
      setRecords(data.records || []);
    } catch (error) {
      console.error('Failed to fetch maintenance records:', error);
      setRecords([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecords();
  }, []);

  const handleScanComplete = (assetId) => {
    setSelectedAssetId(assetId);
    setShowScanner(false);
    setShowFileUpload(false);
    setShowForm(true);
  };

  const handleAddManual = () => {
    setSelectedAssetId(null);
    setShowForm(true);
  };

  return (
    <div className="maintenance-tab">
      <div className="maintenance-header">
        <h2>Maintenance Records</h2>
        <div className="maintenance-actions">
          <button className="md-button" onClick={() => setShowScanner(true)}>
            <span className="material-icons-round">qr_code_scanner</span>
            Scan with Camera
          </button>
          <button className="md-button outline" onClick={() => setShowFileUpload(true)}>
            <span className="material-icons-round">upload_file</span>
            Upload QR Image
          </button>
          <button className="md-button outline" onClick={handleAddManual}>
            <span className="material-icons-round">add</span>
            Add Manually
          </button>
        </div>
      </div>

      {loading ? (
        <div className="loading-message">Loading maintenance records...</div>
      ) : records.length === 0 ? (
        <div className="empty-state">
          <span className="material-icons-round">build</span>
          <p>No maintenance records found</p>
        </div>
      ) : (
        <>
          <div className="desktop-view">
            <MaintenanceTable records={records} />
          </div>
          <div className="mobile-view">
            {records.map(record => (
              <MaintenanceMobileCard key={record.id} record={record} />
            ))}
          </div>
        </>
      )}

      {showScanner && (
        <QRScanner 
          isOpen={showScanner}
          onClose={() => setShowScanner(false)}
          onScanComplete={handleScanComplete}
        />
      )}

      {showFileUpload && (
        <QRFileUpload
          isOpen={showFileUpload}
          onClose={() => setShowFileUpload(false)}
          onScanComplete={handleScanComplete}
        />
      )}

      {showForm && (
        <MaintenanceForm
          isOpen={showForm}
          onClose={() => {
            setShowForm(false);
            setSelectedAssetId(null);
          }}
          assetId={selectedAssetId}
          onSave={() => {
            fetchRecords();
            setShowForm(false);
            setSelectedAssetId(null);
          }}
        />
      )}
    </div>
  );
};

export default MaintenanceTab;
