import React from 'react';

const StatIcon = ({ type }) => {
  // Return relevant Material icon name by stat type
  const icons = {
    totalAssets: "inventory",
    activeAssets: "check_circle",
    maintenanceRecords: "build",
    users: "person",
    scannedToday: "qr_code_scanner"
  };
  return <span className="material-icons-round">{icons[type] || "analytics"}</span>;
};

const StatsCard = ({ label, value, type }) => (
  <div className="stats-card">
    <div className="stats-icon">
      <StatIcon type={type} />
    </div>
    <div className="stats-info">
      <div className="stats-value">{value}</div>
      <div className="stats-label">{label}</div>
    </div>
  </div>
);

export default StatsCard;
