import React, { useState, useEffect } from 'react';
import AssetSearch from './AssetSearch';
import AssetTable from './AssetTable';
import './AssetList.css';
import AssetMobileCard from './AssetMobileCard';
import AddAssetModal from './AddAssetModal';
// import api from '../../services/api';

const AssetList = ({ permissions }) => {
  const [assets, setAssets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredAssets, setFilteredAssets] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editAsset, setEditAsset] = useState(null);

  // Fetch assets from backend API
  const fetchAssets = async () => {
    try {
      const response = await fetch('/api/assets', { credentials: 'include' });
      const data = await response.json();
      
      // Normalize asset data: convert snake_case to camelCase for consistency
      const normalizedAssets = (data.assets || []).map(asset => ({
        ...asset,
        assetId: asset.asset_id || asset.assetId,
        asset_id: asset.asset_id || asset.assetId,
        mfgDate: asset.mfg_date || asset.mfgDate,
        manufacturer: asset.manufacturer,
        type: asset.type,
        status: asset.status,
        hasQR: asset.hasQR || asset.has_qr || false,
      }));
      
      setAssets(normalizedAssets);
      setFilteredAssets(normalizedAssets);
    } catch (error) {
      console.error('Failed to load assets:', error);
      setAssets([]);
      setFilteredAssets([]);
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  // Filter assets based on search term
  useEffect(() => {
    if (!searchTerm.trim()) {
      setFilteredAssets(assets);
    } else {
      const lower = searchTerm.toLowerCase();
      setFilteredAssets(
        assets.filter(a => {
          const assetId = a.assetId || a.asset_id || '';
          const name = a.name || '';
          const location = a.location || '';
          return (
            name.toLowerCase().includes(lower) ||
            assetId.toString().toLowerCase().includes(lower) ||
            location.toLowerCase().includes(lower)
          );
        })
      );
    }
  }, [searchTerm, assets]);

  const openAddModal = () => {
    setEditAsset(null);
    setShowAddModal(true);
  };

  const openEditModal = (asset) => {
    setEditAsset(asset);
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setEditAsset(null);
  };

  const onSaveAsset = () => {
    fetchAssets();
    closeModal();
  };

  return (
    <div className="asset-list">
      <div className="asset-list-header">
        <h2>Asset Management</h2>
        {permissions.canAddAsset && (
          <button className="md-button" onClick={openAddModal}>
            Add New Asset
          </button>
        )}
      </div>

      <AssetSearch searchTerm={searchTerm} setSearchTerm={setSearchTerm} />

      {/* Responsive display: Table on desktop, cards on mobile */}
      <div className="asset-list-content">
        <div className="desktop-view">
          <AssetTable assets={filteredAssets} onEdit={openEditModal} canDelete={permissions.canDeleteAsset} />
        </div>
        <div className="mobile-view">
          {filteredAssets.map(asset => (
            <AssetMobileCard key={asset.assetId} asset={asset} onEdit={openEditModal} canDelete={permissions.canDeleteAsset} />
          ))}
        </div>
      </div>

      {showAddModal && (
        <AddAssetModal 
          isOpen={showAddModal} 
          onClose={closeModal} 
          asset={editAsset} 
          onSave={onSaveAsset} 
        />
      )}
    </div>
  );
};

export default AssetList;
