import React from 'react';
import QRCode from 'qrcode';

const QRActions = ({ data, options }) => {
  // Download as image or svg
  const handleDownload = async () => {
    if (options.format === 'svg') {
      QRCode.toString(data, { type: 'svg', errorCorrectionLevel: options.errorCorrection })
        .then(svgString => {
          const blob = new Blob([svgString], { type: 'image/svg+xml' });
          const url = URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = 'asset-qr.svg';
          a.click();
          URL.revokeObjectURL(url);
        });
    } else {
      QRCode.toDataURL(data, { errorCorrectionLevel: options.errorCorrection })
        .then(url => {
          const a = document.createElement('a');
          a.href = url;
          a.download = 'asset-qr.png';
          a.click();
        });
    }
  };

  // Stub for "Send to Laser"
  const handleSendToLaser = () => {
    alert('Sent to laser marking system (stub).');
  };

  return (
    <div className="qr-actions" style={{ marginTop: 20 }}>
      <button className="md-button" onClick={handleDownload}>
        <span className="material-icons-round">download</span>
        Download QR
      </button>
      <button className="md-button outline" onClick={handleSendToLaser}>
        <span className="material-icons-round">send</span>
        Send to Laser
      </button>
    </div>
  );
};

export default QRActions;
