import React from 'react';
import './Header.css';

const Header = ({ user, onLogout, mobileMenuOpen, toggleMobileMenu, activeTab, onTabChange, allowedTabs }) => {
  const navItems = [
    { id: 'assets', icon: 'inventory', label: 'Asset Management' },
    { id: 'qr-generator', icon: 'qr_code', label: 'QR Generation' },
    { id: 'maintenance', icon: 'build', label: 'Maintenance' },
    { id: 'reports', icon: 'analytics', label: 'Reports & Analytics' },
    { id: 'admin-approvals', icon: 'verified_user', label: 'User Approvals' }
  ];

  return (
    <header className="app-header">
      <div className="header-top">
        <div className="header-left">
          <button 
            className="mobile-menu-toggle"
            onClick={toggleMobileMenu}
            aria-label="Toggle menu"
          >
            <span className="material-icons">
              {mobileMenuOpen ? 'close' : 'menu'}
            </span>
          </button>
          <h1 className="app-title">
            <span className="material-icons title-icon">train</span>
            Railway Asset Management
          </h1>
        </div>
        
        <div className="header-right">
          <div className="user-info">
            <span className="material-icons user-icon">account_circle</span>
            <div className="user-details">
              <span className="user-name">{user?.username || 'User'}</span>
              <span className="user-role">{user?.user_type || 'Role'}</span>
            </div>
          </div>
          <button onClick={onLogout} className="logout-btn">
            <span className="material-icons">logout</span>
            Logout
          </button>
        </div>
      </div>

      <nav className={`navbar ${mobileMenuOpen ? 'mobile-open' : ''}`}>
        {navItems
          .filter(item => allowedTabs.includes(item.id))
          .map((item) => (
            <button
              key={item.id}
              onClick={() => onTabChange(item.id)}
              className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
              id={`nav-${item.id}`}
            >
              <span className="material-icons-round">{item.icon}</span>
              <span className="nav-text">{item.label}</span>
            </button>
          ))}
      </nav>
    </header>
  );
};

export default Header;
